import { FC, useEffect, useState } from 'react';
import { TextField, Button, Stack, Card, Typography, Accordion, AccordionSummary } from '@mui/material';
import { buildQueryString } from '../../utils/build-query-string';
import { useDispatch, useSelector } from 'react-redux';
import { shopsTC } from '../../redux/shops-reducer';
import { useEffectOnce } from '../../common/hooks/useEffectOnce';
import { AppRootStateType, useTypedDispatch } from '../../redux/store';
import { Shop } from '../../redux/types/Shop';

type ShopsFilters = {
  page: number;
  searchTerm?: string;
};

export const Shops: FC = () => {
  const [filters, setFilters] = useState<ShopsFilters>({ searchTerm: 'hemd', page: 1 });
  const shops = useSelector<AppRootStateType, Array<Shop>>((state) => state.shops.shops);
  const dispatch = useTypedDispatch();
  // for the first render
  useEffectOnce(() => {
    dispatch(shopsTC(buildQueryString(filters)));
  });
  useEffect(() => {
    console.log('shops>>>', shops);
  }, [shops.length]);
  return (
    <Stack spacing={2}>
      {shops.map((shop) => (
        // <Card sx={{ padding: 2 }}>
          <Accordion title={shop.shopName}>
            <AccordionSummary
              // expandIcon={<ExpandMoreIcon />}
              // aria-controls="panel1a-content"
              // id="panel1a-header"
            >
              <Typography>Accordion 1</Typography>
            </AccordionSummary>
            <Typography>{shop.shopName}</Typography>
          </Accordion>
        // </Card>
      ))}
    </Stack>
  );
};
