import { Dispatch } from 'redux';
import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { Shop } from './types/shop/shop';
import { shopsApi } from '../api/shops-api';

export type ShopsState = {
  // shops: Array<Shop>;
  shopsRequests: {
    [searchQuery: string]: {
      shops: Array<Shop>;
      totalPageCount: number;
    };
  };
  isLoading: boolean;
  error: string | null;
};

const initialState: ShopsState = {
  shopsRequests: {},
  isLoading: false,
  error: null,
};
const slice = createSlice({
  name: 'shopsReducer',
  initialState: initialState,
  reducers: {
    setShopsAC(state, action: PayloadAction<{ shops: Array<Shop>; totalPageCount: number; searchQuery: string; }>) {
      const { shops, totalPageCount, searchQuery } = action.payload;
      state.shopsRequests = {
        ...state.shopsRequests,
        [searchQuery]: {
          shops,
          totalPageCount,
        }
      };
    },
    setLoadingAC(state, action: PayloadAction<boolean>) {
      state.isLoading = action.payload;
    },
    setErrorAC(state, action: PayloadAction<string | null>) {
      state.error = action.payload;
    },
  }
});

export const shopsReducer = slice.reducer;
export const setShopsAC = slice.actions.setShopsAC;
const setLoadingAC = slice.actions.setLoadingAC;
const setErrorAC = slice.actions.setErrorAC;


export const setShopsTC = (query: string) => (dispatch: Dispatch) => {
  dispatch(setErrorAC(null));
  dispatch(setLoadingAC(true));
  shopsApi.getShops(query)
    .then((res) => {
      dispatch(setShopsAC({
        shops: res?.data?.shops,
        searchQuery: query,
        totalPageCount: res?.data?.metaData?.totalPageCount
      }));
    })
    .catch((error) => {
      dispatch({ type: 'shops/error', payload: error.message });
      dispatch(setErrorAC('Something went wrong:('));
    })
    .finally(() => {
      dispatch(setLoadingAC(false));
    });
};
