import React, { FC, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { AppRootStateType, useTypedDispatch } from '../../redux/store';
import { Shop } from '../../redux/types/Shop';
import { useEffectOnce } from '../../common/hooks/useEffectOnce';
import { shopsTC } from '../../redux/shops-reducer';
import { buildQueryString } from '../../utils/build-query-string';
import { Review } from '../../redux/types/Review';
import { reviewsTC } from '../../redux/reviews-reducer';

type ShopPreviewProps = {
  tsId: string;
};

export const ShopPreview: FC<ShopPreviewProps> = (props) => {
  const reviews = useSelector<AppRootStateType, Array<Review>>(
    (state) => state.reviews.reviews[props.tsId]
  );
  const dispatch = useTypedDispatch();
  // for the first render
  useEffectOnce(() => {
    dispatch(reviewsTC(props.tsId));
  });
  useEffect(() => {
    console.log('shops>>>', reviews);
  }, [reviews]);
  return (
    <div>hello!</div>
  );
};
