import { Stack, CircularProgress, Button, Tabs, Tab, Card } from '@mui/material';
import React, { FC, useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { useEffectOnce } from '../../common/hooks/use-effect-once';
import { ReviewCard } from '../../components/common/review-card';
import { ReviewsState, reviewsTC } from '../../redux/reviews-reducer';
import { AppRootStateType, useTypedDispatch } from '../../redux/store';
import { SortOrder } from '../../utils/common/sort-order';
import { convertToNumber } from '../../utils/convert-to-number';

export const ReviewsList: FC = () => {
  const params = useParams<{ tsId: string; }>();

  const reviewsState = useSelector<AppRootStateType, ReviewsState>(
    (state) => state.reviews
  );
  const [tabsValue, setTabsValue] = useState<SortOrder | 'mostRelevant'>();

  const dispatch = useTypedDispatch();

  useEffectOnce(() => {
    if (!reviewsState[params.tsId as string]) {
      dispatch(reviewsTC(params.tsId as string));
    }
  });

  useEffect(() => {
    console.log('reviews>>>', reviewsState);
  }, [reviewsState]);

  if (reviewsState[params.tsId as string]?.isLoading) return <CircularProgress/>;
  if (reviewsState[params.tsId as string]?.error) return <div>{reviewsState[params.tsId as string].error}</div>;

  return (
    <Stack>
      <Stack spacing={2}>
        <Card>
          <Tabs>
            <Tab label={'Comment date descending'}/>
            <Tab label={'Comment date ascending'}/>
            <Tab label={'Relevance'}/>
          </Tabs>
        </Card>
        {reviewsState?.[params.tsId as string]?.reviews.map((review) => {
          return (
            <ReviewCard
              key={review.UID}
              mark={convertToNumber(review.mark)}
              comment={review.comment || review.markDescription}
              creationDate={review.creationDate}
            />
          );
        })}
      </Stack>
    </Stack>
  );
};
