import { Dispatch } from 'redux';
import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { Shop } from './types/Shop';
import { shopsApi } from '../api/shops-api';
import { Review } from './types/Review';
import { reviewsApi } from '../api/reviews-api';

export type ReviewsState = {
  [tsId: string]: {
    reviews: Array<Review>;
    loading: boolean;
    error: string | null;
  };
};

const initialState: ReviewsState = {};
const slice = createSlice({
  name: 'ReviewsReducer',
  initialState: initialState,
  reducers: {
    setReviewsAC(state, action: PayloadAction<{ tsId: string; reviews: Array<Review>; }>) {
      state[action.payload.tsId].reviews = action.payload.reviews;
    },
    setLoadingAC(state, action: PayloadAction<{ tsId: string; loading: boolean; }>) {
      console.log('action>>', action);
      state[action.payload.tsId].loading = action.payload?.loading;
    },
    setErrorAC(state, action: PayloadAction<{tsId: string; error: string | null;}>) {
      state[action.payload.tsId].error = action.payload.error;
    },
  }
});

export const reviewsReducer = slice.reducer;
export const setReviewsAC = slice.actions.setReviewsAC;
export const setLoadingAC = slice.actions.setLoadingAC;
export const setErrorAC = slice.actions.setErrorAC;


export const reviewsTC = (tsId: string) => (dispatch: Dispatch) => {
  dispatch(setLoadingAC({tsId, loading: true}));
  dispatch(setErrorAC({tsId, error: null}));
  reviewsApi.getReviews(tsId)
    .then((res) => {
      console.log('REVIEWS>>>', res.data.response.data.shop.reviews);
      dispatch(setReviewsAC({ reviews: res.data.response.data?.shop?.reviews, tsId }));
    })
    .catch((error) => {
      dispatch({ type: 'reviews/error', payload: error.message });
      dispatch(setErrorAC({tsId, error: 'Something went wrong:('}));
    })
    .finally(() => {
      dispatch(setLoadingAC({tsId, loading: false}));
    });
};
