import React, { FC, useEffect, useState } from 'react';
import { Accordion, AccordionSummary, Card, CircularProgress, Stack, Typography } from '@mui/material';
import { buildQueryString } from '../../utils/build-query-string';
import { useSelector } from 'react-redux';
import { ShopsState, shopsTC } from '../../redux/shops-reducer';
import { useEffectOnce } from '../../common/hooks/useEffectOnce';
import { AppRootStateType, useTypedDispatch } from '../../redux/store';
import { Shop } from '../../redux/types/Shop';
import { ShopPreview } from './shop-preview';
import { ArrowDown } from '../../components/icons/arrow-down';

type ShopsFilters = {
  page: number;
  searchTerm?: string;
};

export const ShopList: FC = () => {
  const [filters, setFilters] = useState<ShopsFilters>({ searchTerm: 'hemd', page: 1 });
  const shopsState = useSelector<AppRootStateType, ShopsState>((state) => state.shops);
  const dispatch = useTypedDispatch();
  // for the first render
  useEffectOnce(() => {
    dispatch(shopsTC(buildQueryString(filters)));
  });

  if(shopsState.isLoading) return <CircularProgress/>;
  if(shopsState.error) return <Card sx={{padding: 2}}>{shopsState.error}</Card>;

  return (
    <Stack spacing={2}>
      {shopsState.shops.map((shop) => (
        // <Card sx={{ padding: 2 }}>
          <Accordion
            TransitionProps={{ unmountOnExit: true }}
            title={shop.shopName}
            key={shop.tsID}
          >
            <AccordionSummary expandIcon={<ArrowDown/>}>
              <Typography>{shop.shopName}</Typography>
            </AccordionSummary>
            <ShopPreview tsId={shop.tsID}/>
          </Accordion>
        // </Card>
      ))}
    </Stack>
  );
};
