import {
  Accordion,
  AccordionDetails,
  AccordionSummary,
  Card,
  CircularProgress,
  Stack,
  Typography,
  Pagination
} from '@mui/material';
import React, { FC, useState, useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
import { useSearchParams } from 'react-router-dom';
import { useEffectOnce } from '../../common/hooks/use-effect-once';
import { ArrowDown } from '../../components/icons/arrow-down';
import { Rating } from '../../components/rating';
import { ShopsState, setShopsTC } from '../../redux/shops-reducer';
import { AppRootStateType, useTypedDispatch } from '../../redux/store';
import { buildQueryString } from '../../utils/build-query-string';
import { ShopPreview } from './shop-preview';

type ShopsFilters = {
  page: number;
  searchTerm?: string;
};

export const ShopList: FC = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [filters, setFilters] = useState<ShopsFilters>(
    {
      searchTerm: searchParams.get('searchTerm') || 'ama',
      page: Number(searchParams.get('page')),
    }
  );
  const shopsState = useSelector<AppRootStateType, ShopsState>((state) => state.shops);
  const dispatch = useTypedDispatch();
  const isFirstRender = useRef(true);
  // for the first render
  useEffectOnce(() => {
    if(!shopsState.shopsRequests[buildQueryString(filters)]){
      dispatch(setShopsTC(buildQueryString(filters)));
    }
  });

  useEffect(() => {
    console.log('firstRender>>', isFirstRender);
    setSearchParams({
      ...filters,
      page: String(filters.page),
    });
  }, [filters]);

  // useEffect(() => {
  //   if(!shopsState.shopsRequests[buildQueryString(filters)] && !isFirstRender.current){
  //     dispatch(setShopsTC(buildQueryString(filters)));
  //   }
  //   isFirstRender.current = false;
  // }, [filters.page, filters.searchTerm]);

  const setPage = (newPage: number) => {
    setFilters((state) => ({...state, page: newPage}));
  };

  if (shopsState.isLoading) return <CircularProgress/>;
  if (shopsState.error) return <Card sx={{ padding: 2 }}>{shopsState.error}</Card>;

  return (
    <Stack spacing={2} width={'60vw'}>
      {shopsState.error
        ? <Card sx={{ padding: 2 }}>{shopsState.error}</Card>
        : <>
          {shopsState.shopsRequests[buildQueryString(filters)]?.shops.map((shop) => (
            <Accordion
              TransitionProps={{ unmountOnExit: true }}
              title={shop.shopName}
              key={shop.tsID}
            >
              <AccordionSummary expandIcon={<ArrowDown/>}>
                <Stack spacing={2} direction={'row'}>
                  <Typography>{shop.shopName}</Typography>
                  <Rating rating={shop.averageRating}/>
                </Stack>
              </AccordionSummary>
              <AccordionDetails>
                <ShopPreview tsId={shop.tsID}/>
              </AccordionDetails>
            </Accordion>
          ))
          }
        </>
      }
      <Card sx={{padding: 2, display: 'flex', justifyContent: 'center'}}>
        <Pagination
          onChange={(event, value) => setPage(value)}
          page={filters.page}
          count={10}
          color={'primary'}
        />
      </Card>
    </Stack>
  );
};
